import './App.css';
import React, { useState } from 'react';
import axios from "axios";
import '@chatscope/chat-ui-kit-styles/dist/default/styles.min.css';
import { MainContainer, ChatContainer, MessageList, Message, MessageInput, TypingIndicator } from '@chatscope/chat-ui-kit-react';





const App = () => {




  const [prompt35, setPrompt35] = useState('');
  const [message35, setMessage35] = useState('');




  var gptReturn = "";


  const handleSubmit = async (event) => {
    event.preventDefault();
    const apiKey = "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";
    const data = {
      model: "gpt-3.5-turbo",
      messages: [{ role: 'user', content: prompt }],
    };
    const headers = {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${apiKey}`
    };
    const url = 'https://api.openai.com/v1/chat/completions';

    try {
      const completion = await axios.post(url, data, { headers });
      console.log(completion.data);
      setMessage(completion.data.choices[0].message.content);
    } catch (error) {
      console.error(error);
    }
  };



  const askGPT35 = async () => {
    // event.preventDefault();
    const apiKey = "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";
    const data = {
      model: "gpt-3.5-turbo",
      messages: [{ role: 'user', content: prompt35 }],
    };
    const headers = {
      "Content-Type": "application/json",
      'Authorization': `Bearer ${apiKey}`
    };
    const url = 'https://api.openai.com/v1/chat/completions';

    try {
      const completion = await axios.post(url, data, { headers });
      console.log(completion.data);
      gptReturn = completion.data.choices[0].message.content;
    } catch (error) {
      console.error(error);
    }
  };




  const askGPT = async (input) => {
    const apiKey = "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";
    const data = {
      model: 'text-davinci-003',
      prompt: input,
      max_tokens: 50,
      temperature: 0.5
    };
    const headers = {
      "Content-Type": "application/json",
      'Authorization': `Bearer ${apiKey}`
    };
    const url = 'https://api.openai.com/v1/completions';

    try {
      const completion = await axios.post(url, data, { headers });
      gptReturn = completion.data.choices[0].text;
      // console.log(completion.data.choices[0].text);
      console.log(gptReturn);
    } catch (error) {
      console.error(error);
    }
  };



  const CustomComponent = (props) => {


    setPrompt35(props.steps.query.value);
    console.log("set Prompt35.." + props.steps.query.value);

    askGPT35();
    console.log(props.steps.query.value);
    // return (<div>This is what you have type {props.steps.query.value} : {gptReturn} </div>)
    return (<div>{gptReturn} </div>)


  }



  const step = [
    {
      id: "1",
      message: "Hello?",
      trigger: "query",
    },
    {
      id: "query",
      user: true,
      trigger: "3",
    },
    {
      id: "3",
      component: <CustomComponent />, // Mention Custom component here

      asMessage: true,
      trigger: "query",
    },

  ];



  const [count, setCount] = useState(0);


  const [typing, setTyping] = useState(false);

  const [messages, setMessages] = useState([
    {
      message: "Hello, I am ChatGPT! Please ask me anything.",
      sender: "ChatGPT",
      direction: "ingoing",

    },

  ]);


  const handleSend = async (message) => {
    const newMessage = {
      message: message,
      sender: "user",
      direction: "outgoing",
    }

    // new array of messages
    const newMessages = [...messages, newMessage]; // all the old messages, + the new messages 

    //update our messages state
    setMessages(newMessages);

    //set a typing indicator (chatgpt is typing...)
    setTyping(true);
    //process message to chatGPT (send it over and see reponse)

    await processMessageToChatGPT(newMessages);

  }

  async function processMessageToChatGPT(chatMessages) {
    //chatMessages { sender: "user" or "ChatGPT", message: "The message content here"}
    //apiMessages {role: "user" or "assistant", content: "The message content here"}

    let apiMessages = chatMessages.map((messageObject) => {
      let role = "";
      if (messageObject.sender === "ChatGPT") {
        role = "assistant"
      } else {
        role = "user"
      }
      return { role: role, content: messageObject.message }
    });

    //role: "user" -> a message from the user, "assistant -> a response from chatGPT"
    // "system" -> generally one initial message defining HOW we want chatgpt to talk

    const systemMessage = {
      role: "system",
      content: "Explain all concepts like I am a narrator for a digital game."
    }

    const apiRequestBody = {
      "model": "gpt-3.5-turbo",
      "messages": [
        systemMessage,
        ...apiMessages // [message1, message2, message3]
      ]
    }

    const apiKey = "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";


    await fetch("https://api.openai.com/v1/chat/completions", {
      method: "POST",
      headers: {
        'Authorization': `Bearer ${apiKey}`,
        "Content-Type": "application/json"
      },
      body: JSON.stringify(apiRequestBody)
    }).then((data) => {
      return data.json();
    }).then((data) => {
      console.log(data.choices[0].message.content);
      setMessages(
        [...chatMessages, {
          message: data.choices[0].message.content,
          sender: "ChatGPT"
        }]
      );
      setTyping(false);
    })
  }



  return (
    <div>

      <div style={{ position: "relative", height: "800px", width: "700px" }}>

        <MainContainer>
          <ChatContainer>
            <MessageList typingIndicator={typing ? <TypingIndicator content="ChatGPT is typing" /> : null}>
              {messages.map((message, i) => {
                return <Message key={i} model={message} />
              })}
            </MessageList>
            <MessageInput placeholder='Type message here' onSend={handleSend} />
          </ChatContainer>
        </MainContainer>

      </div>




      <div>
        <form onSubmit={handleSubmit}>
          <label>
            Prompt:
            <input type="text" value={prompt} onChange={(event) => setPrompt(event.target.value)} />
          </label>
          <button type="submit">Submit</button>
        </form>
        {message && (
          <div>
            <p>{message}</p>
          </div>
        )}
      </div>



    </div>
  );
};

export default App;
