import './App.css';
import React, { useState } from 'react';
import axios from "axios";
import openai from 'openai';

import ChatBot from 'react-simple-chatbot';
import { Segment } from 'semantic-ui-react';
import { ThemeProvider } from "styled-components";





// Creating our own theme
const theme = {
  background: '#C9FF8F',
  headerBgColor: '#197B22',
  headerFontSize: '20px',
  botBubbleColor: '#0F3789',
  headerFontColor: 'white',
  botFontColor: 'white',
  userBubbleColor: '#FF5733',
  userFontColor: 'white',
};


// Set some properties of the bot
const config = {
  botAvatar: "img.png",
  floating: true,
};





const App = () => {
  //new ChatGPT API
  // const apiKey = "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";
  // const data = {
  //   model: 'text-davinci-003',
  //   prompt: 'Say this is a test',
  //   temperature: 0,
  //   max_tokens: 1000
  // };
  // const headers = {
  //   'Content-Type': 'application/json',
  //   'Authorization': `Bearer ${apiKey}`
  // };
  // const url = 'https://api.openai.com/v1/completions';



  const [prompt, setPrompt] = useState('Say this is a test');
  const [message, setMessage] = useState('');

  const [gptfeed, setGptfeed] = useState('');



  const [prompt35, setPrompt35] = useState('');
  const [message35, setMessage35] = useState('');




  var gptReturn = "";


  const handleSubmit = async (event) => {
    event.preventDefault();
    const apiKey = "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";
    const data = {
      model: "gpt-3.5-turbo",
      messages: [{ role: 'user', content: prompt }],
    };
    const headers = {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${apiKey}`
    };
    const url = 'https://api.openai.com/v1/chat/completions';

    try {
      const completion = await axios.post(url, data, { headers });
      console.log(completion.data);
      setMessage(completion.data.choices[0].message.content);
    } catch (error) {
      console.error(error);
    }
  };



  const askGPT35 = async ( ) => {
    // event.preventDefault();
    const apiKey = "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";
    const data = {
      model: "gpt-3.5-turbo",
      messages: [{ role: 'user', content: prompt35 }],
    };
    const headers = {
      "Content-Type": "application/json",
      'Authorization': `Bearer ${apiKey}`
    };
    const url = 'https://api.openai.com/v1/chat/completions';

    try {
      const completion = await axios.post(url, data, { headers });
      console.log(completion.data);
      gptReturn= completion.data.choices[0].message.content;
    } catch (error) {
      console.error(error);
    }
  };




  const askGPT = async (input) => {
    const apiKey = "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";
    const data = {
      model: 'text-davinci-003',
      prompt: input,
      max_tokens: 50,
      temperature: 0.5
    };
    const headers = {
      "Content-Type": "application/json",
      'Authorization': `Bearer ${apiKey}`
    };
    const url = 'https://api.openai.com/v1/completions';

    try {
      const completion = await axios.post(url, data, { headers });
      gptReturn = completion.data.choices[0].text;
      // console.log(completion.data.choices[0].text);
      console.log(gptReturn);
    } catch (error) {
      console.error(error);
    }
  };



  const CustomComponent = (props) => {


    setPrompt35(props.steps.query.value);
    console.log("set Prompt35.." + props.steps.query.value);

    askGPT35();
    console.log(props.steps.query.value);
    // return (<div>This is what you have type {props.steps.query.value} : {gptReturn} </div>)
    return (<div>{gptReturn} </div>)


  }



  const step = [
    {
      id: "1",
      message: "Hello?",
      trigger: "query",
    },
    {
      id: "query",
      user: true,
      trigger: "3",
    },
    {
      id: "3",
      component: <CustomComponent  />, // Mention Custom component here

      asMessage: true,
      trigger: "query",
    },

  ];




  return (
    <div>




      <div>
        <form onSubmit={handleSubmit}>
          <label>
            Prompt:
            <input type="text" value={prompt} onChange={(event) => setPrompt(event.target.value)} />
          </label>
          <button type="submit">Submit</button>
        </form>
        {message && (
          <div>
            <p>{message}</p>
          </div>
        )}
      </div>



    </div>
  );
};

export default App;
